"use strict";

var _size = require("./size");

describe('size', function () {
  it('tests `isSizeLarge` behavior', function () {
    expect((0, _size.isSizeLarge)('lg')).toBe(true);
    expect((0, _size.isSizeLarge)()).toBe(false);
    expect((0, _size.isSizeLarge)(null)).toBe(false);
    expect((0, _size.isSizeLarge)('lrg')).toBe(false);
  });
  it('tests `isSizeSmall` behavior', function () {
    expect((0, _size.isSizeSmall)('sm')).toBe(true);
    expect((0, _size.isSizeSmall)()).toBe(false);
    expect((0, _size.isSizeSmall)(null)).toBe(false);
    expect((0, _size.isSizeSmall)('sml')).toBe(false);
  });
});